import React from 'react';

const initialData = {
  name: 'Excessive operation',
  status: 'new',
  assignee: 'bill'
}


const IssueCard = ({data = initialData}) => {
  const [issue, setIssue] = React.useState(data)

  return (
    <div>
      <p>Name: {issue.name}</p>
      <p>Assignee: {issue.assignee}</p>
      <p>Status: {issue.status}</p>
      <p>Actions</p>
      <button>Approve</button>
      <button>On Hold</button>
      <button>Reject</button>
      <button>Done</button>
    </div>
  )
}

export default IssueCard
