import React from 'react';
import {useMachine} from '@xstate/react'
import actionMachine from '../stateMachines/actionMachine'

const initialData = {
  name: 'Excessive operation',
  status: 'new',
  assignee: 'bill'
}

const ActionButton = ({action, send}) => {
  return (
    <button onClick={() => send(action)}>{action}</button>
  )
}

const IssueCard = ({data = initialData}) => {
  const [issue, setIssue] = React.useState(data)

  const [current, send] = useMachine(actionMachine)


  return (
    <div>
      <p>Name: {issue.name}</p>
      <p>Assignee: {issue.assignee}</p>
      <p>Status: {issue.status}</p>
      <p>Actions</p>

      <small>
        <p>current.value: {JSON.stringify(current.value)}</p>
      </small>      

      {current.matches('status.new') &&
        <>
          <ActionButton action="APPROVE" send={send}/>
          <ActionButton action="REJECT" send={send}/>
          <ActionButton action="HOLD" send={send}/>
        </>
      }

    </div>
  )
}

export default IssueCard
