import React from 'react';

const initialData = {
  name: 'Excessive operation',
  status: 'new',
  assignee: 'bill'
}


const IssueCard = ({data = initialData}) => {
  const [issue, setIssue] = React.useState(data)
  const setStatus = (status) => setIssue({...issue, status})

  const isNew = issue.status === 'new'

  return (
    <div>
      <p>Name: {issue.name}</p>
      <p>Assignee: {issue.assignee}</p>
      <p>Status: {issue.status}</p>
      <p>Actions</p>

      {isNew &&
      <button onClick={() => setStatus('onHold')}>
        On Hold
      </button>
      }

      <button onClick={() => setStatus('notDoing')}>
        Reject
      </button>

      <button onClick={() => setStatus('resolved')}>
        Done
      </button>
    </div>
  )
}

export default IssueCard
