import React from 'react';
import IssueCard from './IssueCard'
import {sortBy} from 'lodash';

const initialData = [
  {
    name: 'Excessive operation',
    status: 'In Progress',
    assignee: 'bill'
  },
  {
    name: 'Status Mismatch',
    status: 'New',
    assignee: 'bob'
  },
  {
    name: 'Food court open',
    status: 'Resolved',
    assignee: 'jane'
  },
  {
    name: 'Magic mountain',
    status: 'New',
    assignee: 'billy'
  },
  {
    name: 'Turn gas office',
    status: 'Not Doing',
    assignee: 'jason'
  },
  {
    name: 'Stage 4 Restriction',
    status: 'On Hold',
    assignee: 'teddy'
  }
]

const sortByStatus = (data) => {
  const sortRank = {
    "New": 1,
    "In Progress": 2,
    "On Hold": 3,
    "Resolved": 4,
    "Not Doing": 5
  }

  return sortBy(data, function(el){
    return sortRank[el.status]
  })
}


const IssueList = ({data = initialData}) => {
  // React Issue State
  const [issues, setIssues] = React.useState(sortByStatus(data))

  return (
    <div>
        {issues.map(issue => <IssueCard data={issue}/>)}
    </div>
  )
}

export default IssueList
