import {Machine} from 'xstate'

const actionMachine = Machine({
  id: 'machine',
  initial: 'status',
  states: {
    status: {
      initial: 'new',
      states: {
        new: {
          on: {
            APPROVE: 'inProgress',
            REJECT: 'notDoing',
            HOLD: 'onHold'
          }
        },
        inProgress: {
          on: {
            REJECT: 'notDoing',
            HOLD: 'onHold',
            DONE: 'resolved'
          }
        },
        onHold: {
          on: {
            APPROVE: 'inProgress',
            REJECT: 'notDoing'
          }
        },
        notDoing: {
          on: {
            APPROVE: 'inProgress',
            HOLD: 'onHold'
          }
        },
        resolved: {
          type: 'final'
        }
      }
    }
  }
});

export default actionMachine
