import React from 'react';
import IssueCard from './IssueCard'
import {orderBy} from 'lodash';

const initialData = [
  {
    name: 'Excessive operation',
    status: 'In Progress',
    assignee: 'bill'
  },
  {
    name: 'Status Mismatch',
    status: 'New',
    assignee: 'bob'
  },
  {
    name: 'Food court open',
    status: 'Resolved',
    assignee: 'jane'
  },
  {
    name: 'Magic mountain',
    status: 'New',
    assignee: 'billy'
  },
  {
    name: 'Turn gas office',
    status: 'Not Doing',
    assignee: 'jason'
  },
  {
    name: 'Stage 4 Restriction',
    status: 'On Hold',
    assignee: 'teddy'
  }
]



const IssueList = ({data = initialData}) => {
  // React Issue State
  const sortedData = orderBy(sortedData, 'status', 'asc')
  const [issues, setIssues] = React.useState(sortedData)

  return (
    <div>
        {issues.map(issue => <IssueCard data={issue}/>)}
    </div>
  )
}

export default IssueList
