import {Machine, assign} from 'xstate'

const actionMachine = Machine({
  id: 'machine',
  initial: 'status',
  context: {
    status: 'New'
  },
  states: {
    status: {
      initial: 'new',
      states: {
        new: {
          on: {
            APPROVE: 'inProgress',
            REJECT: 'notDoing',
            HOLD: 'onHold'
          },
          entry: assign({
            status: 'New'
          })
        },
        inProgress: {
          on: {
            REJECT: 'notDoing',
            HOLD: 'onHold',
            DONE: 'resolved'
          },
          entry: assign({
            status: 'In Progress'
          })
        },
        onHold: {
          on: {
            APPROVE: 'inProgress',
            REJECT: 'notDoing'
          },
          entry: assign({
            status: 'On Hold'
          })
        },
        notDoing: {
          on: {
            APPROVE: 'inProgress',
            HOLD: 'onHold'
          },
          entry: assign({
            status: 'Not Doing'
          })
        },
        resolved: {
          type: 'final',
          entry: assign({
            status: 'Resolved'
          })          
        }
      }
    }
  }
});


export default actionMachine
